import React, { useState, useEffect } from "react";
import './IndexBody.css';
import topImage from "../../images/capanno.jpg";

export default function IndexBody() {
    const [loaded, setLoaded] = useState(false);
    const [showText, setShowText] = useState(false);
    const [scrollY, setScrollY] = useState(0);

    useEffect(() => {
        const img = new Image();
        img.src = topImage;
        img.onload = () => {
            setLoaded(true);
        };
    }, []);

    useEffect(() => {
        // Dopo un certo ritardo (ad esempio, 100ms), aggiungi la classe show
        const timeout = setTimeout(() => {
            setShowText(true);
        }, 100);

        // Pulisci il timeout nel caso in cui il componente venga smontato prima dell'attivazione
        return () => clearTimeout(timeout);
    }, []);

    useEffect(() => {
        const handleScroll = () => {
            const currentScrollY = window.scrollY;

            if (currentScrollY > scrollY) {
                setShowText(false);
              } else if (currentScrollY < scrollY) {
                console.log('Lo scroll è in su!');
              }
        };

        window.addEventListener('scroll', handleScroll);

      }, []); 

    return (
        <>
            <div id="container">
                <div className={`top-part ${loaded ? 'loaded' : ''}`}>
                    <img className="top-image" src={topImage} alt="NON VA NULLA EHEAD" />
                    <div className="top-part-text">
                        <div className={`top-text ${showText ? 'show' : ''}`}>
                            <h3>T.R.L. Express S.R.L.</h3>
                            <p>Testo da aggiungere etc etc</p>
                        </div>

                    </div>
                </div>
            </div>
        </>
    );
}
