import React from 'react';
import Select from 'react-select';
import './ExRoutes.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTruck, faEuroSign, faTruckMoving, faTruckField } from '@fortawesome/free-solid-svg-icons';
import styled from 'styled-components';
import Title from '../helpers/Title';

const StyledTable = styled.table`
    width: 100%;
    border-collapse: collapse;
    margin: auto;

    th, td {
    border: 1px solid #ddd;
    padding: 8px;
    text-align: left;
    }

    th {
    background-color: #f2f2f2;
    }

    tbody tr:nth-child(even) {
    background-color: #f9f9f9;
    }

    tbody tr:hover {
    background-color: #f5f5f5;
    }
`;

const TruckSelect = ({ options, handleSelectTruck }) => {
    return (
        <Select options={options} onChange={handleSelectTruck} />
    );
};
const StyledContainer = styled.div`
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-column-gap: 0px;
    grid-row-gap: 0px;
`;

const options = [
    {
        label: (
          <div>
            Furgone piccolo () <FontAwesomeIcon icon={faTruckField} className="icon" />
          </div>
        ),
        value: 'daily',
      },
    {
      label: (
        <div>
          IVECO Daily <FontAwesomeIcon icon={faTruck} className="icon" />
        </div>
      ),
      value: 'daily',
    },
    {
        label: (
          <div>
            Telonato <FontAwesomeIcon icon={faTruckMoving} className="icon" />
          </div>
        ),
        value: 'telonato',
      },
  ];

const handleSelectTruck = () => {

};

export default function ExRoutes() {
    return (
        <>
            <Title title="Tratte esempio" />
            <div className='page-header'>
                <h3>Esempi di tratta</h3>
            </div>
            <StyledContainer className='page-container'>
                <div className='explanation-container'>
                    <p>Di seguito, alcune tratte indicative che possono servirvi come indicatore sul prezzo medio del trasporto.</p>
                </div>
                <div className='ex-routes-container'>
                    <StyledTable>
                        <thead>
                            <tr>
                                <th>Tratta</th>
                                <th>Mezzo</th>
                                <th>Costo</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>Milano - Roma</td>
                                <td>
                                    <TruckSelect options={options} onChange={handleSelectTruck}>
                                        {/* <option value="telonato">Telonato <FontAwesomeIcon icon={faTruck} className="icon" /></option>
                                        <option value="furgone">Furgone <FontAwesomeIcon icon={faTruck} className="icon" /></option>
                                        <option value="auto">Auto <FontAwesomeIcon icon={faTruck} className="icon" /></option> */}
                                    </TruckSelect>
                                </td>
                                <td><span>50</span> <FontAwesomeIcon icon={faEuroSign} className="icon" /></td>
                            </tr>
                        </tbody>
                    </StyledTable>
                </div>
            </StyledContainer>
        </>
    );
};
