import React from 'react';
import './ExRoutes.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTruck, faEuroSign } from '@fortawesome/free-solid-svg-icons';
import styled from 'styled-components';


const StyledTable = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 20px;

  th, td {
    border: 1px solid #ddd;
    padding: 8px;
    text-align: left;
  }

  th {
    background-color: #f2f2f2;
  }

  tbody tr:nth-child(even) {
    background-color: #f9f9f9;
  }

  tbody tr:hover {
    background-color: #f5f5f5;
  }
`;
export default function ExRoutes() {
    return (
        <>
            <div className='page-container'>

                <div className='ex-routes-container'>
                    <div className='route'>
                        <StyledTable>
                            <thead>
                                <tr>
                                    <th>Tratta</th>
                                    <th>Mezzo</th>
                                    <th>Costo</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>Milano - Roma</td>
                                    <td> Telonato <FontAwesomeIcon icon={faTruck} className="icon" /></td>
                                    <td> 50<FontAwesomeIcon icon={faEuroSign} className="icon" /></td>
                                </tr>
                            </tbody>
                        </StyledTable>
                    </div>
                </div>
            </div>
        </>
    );
};
