import React from 'react';
import './ExRoutes.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTruck, faEuroSign } from '@fortawesome/free-solid-svg-icons';
import styled from 'styled-components';
import Title from '../helpers/Title';

const StyledTable = styled.table`
    width: 100%;
    border-collapse: collapse;
    margin: auto;

    th, td {
    border: 1px solid #ddd;
    padding: 8px;
    text-align: left;
    }

    th {
    background-color: #f2f2f2;
    }

    tbody tr:nth-child(even) {
    background-color: #f9f9f9;
    }

    tbody tr:hover {
    background-color: #f5f5f5;
    }
`;
const StyledSelect = styled.select`
  padding: 8px;
  font-size: 14px;
  border: 1px solid #ccc;
  border-radius: 4px;
  cursor: pointer;

  .icon {
    margin-left: 8px;
  }
`;
const StyledContainer = styled.div`
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-column-gap: 0px;
    grid-row-gap: 0px;
`;

const handleSelectTruck = () => {

};

export default function ExRoutes() {
    return (
        <>
            <Title title="Tratte esempio" />
            <div className='page-header'>
                <h3>Esempi di tratta</h3>
            </div>
            <StyledContainer className='page-container'>
                <div className='explanation-container'>
                    <p>Di seguito, alcune tratte indicative che possono servirvi come indicatore sul prezzo medio del trasporto.</p>
                </div>
                <div className='ex-routes-container'>
                    <StyledTable>
                        <thead>
                            <tr>
                                <th>Tratta</th>
                                <th>Mezzo</th>
                                <th>Costo</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>Milano - Roma</td>
                                <td>
                                    <select onChange={handleSelectTruck}>
                                        <option value="telonato">Telonato <FontAwesomeIcon icon={faTruck} className="icon" /></option>
                                    </select>
                                </td>
                                <td><span>50</span> <FontAwesomeIcon icon={faEuroSign} className="icon" /></td>
                            </tr>
                        </tbody>
                    </StyledTable>
                </div>
            </StyledContainer>
        </>
    );
};
